    21→    e.preventDefault();
    22→    setError(null);
    23→    setIsLoading(true);
    24→
    25→    // Simular delay de autenticacao
    26→    await new Promise(resolve => setTimeout(resolve, 500));
    27→
    28→    // Verificar credenciais
    29→    if (formData.username === ADMIN_USERNAME && formData.password === ADMIN_PASSWORD) {
