    31→      localStorage.setItem('admin_authenticated', 'true');
    32→      localStorage.setItem('admin_login_time', new Date().toISOString());
    33→
    34→      // Redirecionar para o admin
    35→      router.push('/admin');
    36→      router.refresh();
    37→    } else {
    38→      setError('Usuario ou senha incorretos');
    39→      setIsLoading(false);
    40→    }
    41→  };
