     1→'use client';
     2→
     3→import { useState, useCallback, useTransition } from 'react';
     4→import { useRouter } from 'next/navigation';
     5→import {
     6→  createPost,
     7→  approvePost,
     8→  rejectPost,
     9→  likePost,
    10→  commentPost,
    11→  deletePost,
    12→} from '@/actions/posts';
    13→import type { CreatePostData } from '@/types/post';
    14→
    15→export function usePosts() {
    16→  const router = useRouter();
    17→  const [isPending, startTransition] = useTransition();
    18→  const [error, setError] = useState<string | null>(null);
    19→
    20→  const handleCreate = useCallback(async (data: CreatePostData) => {
    21→    setError(null);
    22→    const result = await createPost(data);
    23→
    24→    if (result.error) {
    25→      setError(result.error);
    26→      return { success: false, error: result.error };
    27→    }
    28→
    29→    startTransition(() => {
    30→      router.refresh();
    31→    });
    32→
    33→    return { success: true, data: result.data };
    34→  }, [router]);
    35→
    36→  const handleApprove = useCallback(async (postId: string) => {
    37→    setError(null);
    38→    const result = await approvePost(postId);
    39→
    40→    if (result.error) {
    41→      setError(result.error);
    42→      return { success: false, error: result.error };
    43→    }
    44→
    45→    startTransition(() => {
    46→      router.refresh();
    47→    });
    48→
    49→    return { success: true };
    50→  }, [router]);
    51→
    52→  const handleReject = useCallback(async (postId: string, reason: string) => {
    53→    setError(null);
    54→    const result = await rejectPost(postId, reason);
    55→
    56→    if (result.error) {
    57→      setError(result.error);
    58→      return { success: false, error: result.error };
    59→    }
    60→
    61→    startTransition(() => {
    62→      router.refresh();
    63→    });
    64→
    65→    return { success: true };
    66→  }, [router]);
    67→
    68→  const handleLike = useCallback(async (postId: string) => {
    69→    setError(null);
    70→    const result = await likePost(postId);
    71→
    72→    if (result.error) {
    73→      setError(result.error);
    74→      return { success: false, error: result.error };
    75→    }
    76→
    77→    startTransition(() => {
    78→      router.refresh();
    79→    });
    80→
    81→    return { success: true };
    82→  }, [router]);
    83→
    84→  const handleComment = useCallback(async (postId: string, content: string) => {
    85→    setError(null);
    86→    const result = await commentPost(postId, content);
    87→
    88→    if (result.error) {
    89→      setError(result.error);
    90→      return { success: false, error: result.error };
    91→    }
    92→
    93→    startTransition(() => {
    94→      router.refresh();
    95→    });
    96→
    97→    return { success: true, data: result.data };
    98→  }, [router]);
    99→
   100→  const handleDelete = useCallback(async (postId: string) => {
   101→    setError(null);
   102→    const result = await deletePost(postId);
   103→
   104→    if (result.error) {
   105→      setError(result.error);
   106→      return { success: false, error: result.error };
   107→    }
   108→
   109→    startTransition(() => {
   110→      router.refresh();
   111→    });
   112→
   113→    return { success: true };
   114→  }, [router]);
   115→
   116→  return {
   117→    isPending,
   118→    error,
   119→    create: handleCreate,
   120→    approve: handleApprove,
   121→    reject: handleReject,
   122→    like: handleLike,
   123→    comment: handleComment,
   124→    delete: handleDelete,
   125→  };
   126→}
