     1→'use server';
     2→
     3→import { createClient } from '@/lib/supabase/server';
     4→import { revalidatePath } from 'next/cache';
     5→
     6→type ActionResponse = {
     7→  error?: string;
     8→  success?: boolean;
     9→  data?: any;
    10→};
    11→
    12→/**
    13→ * Inscrever-se em um evento
    14→ */
    15→export async function registerForEvent(eventId: string): Promise<ActionResponse> {
    16→  try {
    17→    const supabase = await createClient();
    18→
    19→    const { data: { user } } = await supabase.auth.getUser();
    20→    if (!user) {
    21→      return { error: 'Usuario nao autenticado' };
    22→    }
    23→
    24→    // Verificar se o evento existe e esta ativo
    25→    const { data: event, error: eventError } = await supabase
    26→      .from('events')
    27→      .select('*')
    28→      .eq('id', eventId)
    29→      .eq('is_active', true)
    30→      .single();
    31→
    32→    if (eventError || !event) {
    33→      return { error: 'Evento nao encontrado' };
    34→    }
    35→
    36→    // Verificar se o evento ja passou
    37→    if (new Date(event.end_time) < new Date()) {
    38→      return { error: 'Este evento ja foi encerrado' };
    39→    }
    40→
    41→    // Verificar nivel do usuario
    42→    const { data: userProfile } = await supabase
    43→      .from('users')
    44→      .select('advocate_level')
    45→      .eq('id', user.id)
    46→      .single();
    47→
    48→    if (userProfile && event.required_level > (userProfile.advocate_level || 1)) {
    49→      return { error: `Nivel minimo requerido: ${event.required_level}` };
    50→    }
    51→
    52→    // Verificar se ja esta inscrito
    53→    const { data: existingReg } = await supabase
    54→      .from('event_registrations')
    55→      .select('id, status')
    56→      .eq('event_id', eventId)
    57→      .eq('user_id', user.id)
    58→      .single();
    59→
    60→    if (existingReg && existingReg.status !== 'cancelled') {
    61→      return { error: 'Voce ja esta inscrito neste evento' };
    62→    }
    63→
    64→    // Verificar vagas disponiveis
    65→    if (event.max_participants) {
    66→      const { count } = await supabase
    67→        .from('event_registrations')
    68→        .select('id', { count: 'exact' })
    69→        .eq('event_id', eventId)
    70→        .neq('status', 'cancelled');
    71→
    72→      if ((count || 0) >= event.max_participants) {
    73→        return { error: 'Nao ha mais vagas disponiveis' };
    74→      }
    75→    }
    76→
    77→    // Criar inscricao ou reativar inscricao cancelada
    78→    if (existingReg) {
    79→      const { error } = await supabase
    80→        .from('event_registrations')
    81→        .update({ status: 'registered', registration_time: new Date().toISOString() })
    82→        .eq('id', existingReg.id);
    83→
    84→      if (error) {
    85→        return { error: 'Erro ao reativar inscricao' };
    86→      }
    87→    } else {
    88→      const { error } = await supabase
    89→        .from('event_registrations')
    90→        .insert({
    91→          event_id: eventId,
    92→          user_id: user.id,
    93→          status: 'registered',
    94→        });
    95→
    96→      if (error) {
    97→        return { error: 'Erro ao realizar inscricao' };
    98→      }
    99→    }
   100→
   101→    revalidatePath('/eventos');
   102→    revalidatePath('/dashboard');
   103→    return { success: true };
   104→  } catch {
   105→    return { error: 'Erro interno do servidor' };
   106→  }
   107→}
   108→
   109→/**
   110→ * Cancelar inscricao em um evento
   111→ */
   112→export async function cancelEventRegistration(eventId: string): Promise<ActionResponse> {
   113→  try {
   114→    const supabase = await createClient();
   115→
   116→    const { data: { user } } = await supabase.auth.getUser();
   117→    if (!user) {
   118→      return { error: 'Usuario nao autenticado' };
   119→    }
   120→
   121→    const { error } = await supabase
   122→      .from('event_registrations')
   123→      .update({ status: 'cancelled' })
   124→      .eq('event_id', eventId)
   125→      .eq('user_id', user.id);
   126→
   127→    if (error) {
   128→      return { error: 'Erro ao cancelar inscricao' };
   129→    }
   130→
   131→    revalidatePath('/eventos');
   132→    revalidatePath('/dashboard');
   133→    return { success: true };
   134→  } catch {
   135→    return { error: 'Erro interno do servidor' };
   136→  }
   137→}
   138→
   139→/**
   140→ * Confirmar presenca em evento (check-in)
   141→ */
   142→export async function checkInEvent(eventId: string): Promise<ActionResponse> {
   143→  try {
   144→    const supabase = await createClient();
   145→
   146→    const { data: { user } } = await supabase.auth.getUser();
   147→    if (!user) {
   148→      return { error: 'Usuario nao autenticado' };
   149→    }
   150→
   151→    const { error } = await supabase
   152→      .from('event_registrations')
   153→      .update({
   154→        status: 'attended',
   155→        check_in_time: new Date().toISOString(),
   156→      })
   157→      .eq('event_id', eventId)
   158→      .eq('user_id', user.id);
   159→
   160→    if (error) {
   161→      return { error: 'Erro ao realizar check-in' };
   162→    }
   163→
   164→    revalidatePath('/eventos');
   165→    return { success: true };
   166→  } catch {
   167→    return { error: 'Erro interno do servidor' };
   168→  }
   169→}
   170→
   171→/**
   172→ * Enviar feedback de evento
   173→ */
   174→export async function submitEventFeedback(
   175→  eventId: string,
   176→  feedback: string
   177→): Promise<ActionResponse> {
   178→  try {
   179→    const supabase = await createClient();
   180→
   181→    const { data: { user } } = await supabase.auth.getUser();
   182→    if (!user) {
   183→      return { error: 'Usuario nao autenticado' };
   184→    }
   185→
   186→    if (!feedback.trim()) {
   187→      return { error: 'Feedback nao pode ser vazio' };
   188→    }
   189→
   190→    const { error } = await supabase
   191→      .from('event_registrations')
   192→      .update({ feedback: feedback.trim() })
   193→      .eq('event_id', eventId)
   194→      .eq('user_id', user.id);
   195→
   196→    if (error) {
   197→      return { error: 'Erro ao enviar feedback' };
   198→    }
   199→
   200→    revalidatePath('/eventos');
   201→    return { success: true };
   202→  } catch {
   203→    return { error: 'Erro interno do servidor' };
   204→  }
   205→}
   206→
   207→// ============ ADMIN ACTIONS ============
   208→
   209→/**
   210→ * Criar evento (admin)
   211→ */
   212→export async function createEvent(data: {
   213→  title: string;
   214→  description?: string | null;
   215→  type: 'virtual' | 'presencial' | 'hibrido';
   216→  location?: string | null;
   217→  starts_at: string;
   218→  ends_at?: string | null;
   219→  max_participants?: number | null;
   220→  meeting_url?: string | null;
   221→  image_url?: string | null;
   222→}): Promise<ActionResponse> {
   223→  try {
   224→    const supabase = await createClient();
   225→
   226→    const { data: { user } } = await supabase.auth.getUser();
   227→    if (!user) {
   228→      return { error: 'Usuario nao autenticado' };
   229→    }
   230→
   231→    // Verificar se e admin/creator
   232→    const { data: profile } = await supabase
   233→      .from('profiles')
   234→      .select('role, is_creator')
   235→      .eq('id', user.id)
   236→      .single();
   237→
   238→    if (!profile || (profile.role !== 'admin' && !profile.is_creator)) {
   239→      return { error: 'Acesso nao autorizado' };
   240→    }
   241→
   242→    const { data: event, error } = await supabase
   243→      .from('events')
   244→      .insert({
   245→        title: data.title,
   246→        description: data.description || null,
   247→        type: data.type,
   248→        location: data.location || null,
   249→        starts_at: data.starts_at,
   250→        ends_at: data.ends_at || null,
   251→        max_participants: data.max_participants || null,
   252→        meeting_url: data.meeting_url || null,
   253→        image_url: data.image_url || null,
   254→        is_active: true,
   255→      })
   256→      .select()
   257→      .single();
   258→
   259→    if (error) {
   260→      console.error('Error creating event:', error);
   261→      return { error: 'Erro ao criar evento' };
   262→    }
   263→
   264→    revalidatePath('/eventos');
   265→    revalidatePath('/admin/eventos');
   266→    return { success: true, data: event };
   267→  } catch {
   268→    return { error: 'Erro interno do servidor' };
   269→  }
   270→}
   271→
   272→/**
   273→ * Ativar/Desativar evento (admin)
   274→ */
   275→export async function toggleEventActive(
   276→  eventId: string,
   277→  isActive: boolean
   278→): Promise<ActionResponse> {
   279→  try {
   280→    const supabase = await createClient();
   281→
   282→    const { data: { user } } = await supabase.auth.getUser();
