    15→export async function registerForEvent(eventId: string): Promise<ActionResponse> {
    16→  try {
    17→    const supabase = await createClient();
    18→
    19→    const { data: { user } } = await supabase.auth.getUser();
    20→    if (!user) {
    21→      return { error: 'Usuario nao autenticado' };
    22→    }
    23→
    24→    // Verificar se o evento existe e esta ativo
    25→    const { data: event, error: eventError } = await supabase
    26→      .from('events')
    27→      .select('*')
    28→      .eq('id', eventId)
    29→      .eq('is_active', true)
    30→      .single();
    31→
    32→    if (eventError || !event) {
    33→      return { error: 'Evento nao encontrado' };
    34→    }
    35→
    36→    // Verificar se o evento ja passou
    37→    if (new Date(event.end_time) < new Date()) {
    38→      return { error: 'Este evento ja foi encerrado' };
    39→    }
    40→
    41→    // Verificar nivel do usuario
    42→    const { data: userProfile } = await supabase
    43→      .from('users')
    44→      .select('advocate_level')
    45→      .eq('id', user.id)
    46→      .single();
    47→
    48→    if (userProfile && event.required_level > (userProfile.advocate_level || 1)) {
    49→      return { error: `Nivel minimo requerido: ${event.required_level}` };
    50→    }
    51→
    52→    // Verificar se ja esta inscrito
    53→    const { data: existingReg } = await supabase
    54→      .from('event_registrations')
    55→      .select('id, status')
    56→      .eq('event_id', eventId)
    57→      .eq('user_id', user.id)
    58→      .single();
    59→
    60→    if (existingReg && existingReg.status !== 'cancelled') {
    61→      return { error: 'Voce ja esta inscrito neste evento' };
    62→    }
    63→
    64→    // Verificar vagas disponiveis
    65→    if (event.max_participants) {
    66→      const { count } = await supabase
    67→        .from('event_registrations')
    68→        .select('id', { count: 'exact' })
    69→        .eq('event_id', eventId)
    70→        .neq('status', 'cancelled');
    71→
    72→      if ((count || 0) >= event.max_participants) {
    73→        return { error: 'Nao ha mais vagas disponiveis' };
    74→      }
    75→    }
    76→
    77→    // Criar inscricao ou reativar inscricao cancelada
    78→    if (existingReg) {
    79→      const { error } = await supabase
    80→        .from('event_registrations')
    81→        .update({ status: 'registered', registration_time: new Date().toISOString() })
    82→        .eq('id', existingReg.id);
    83→
    84→      if (error) {
    85→        return { error: 'Erro ao reativar inscricao' };
    86→      }
    87→    } else {
    88→      const { error } = await supabase
    89→        .from('event_registrations')
    90→        .insert({
    91→          event_id: eventId,
    92→          user_id: user.id,
    93→          status: 'registered',
    94→        });
    95→
    96→      if (error) {
    97→        return { error: 'Erro ao realizar inscricao' };
    98→      }
