    52â†’// Cores de fundo para fallback baseadas no nome
    53â†’const backgroundColors = [
    54â†’  'bg-primary-500',
    55â†’  'bg-emerald-500',
    56â†’  'bg-amber-500',
    57â†’  'bg-rose-500',
    58â†’  'bg-violet-500',
