    64â†’// FunÃ§Ã£o para extrair iniciais do nome
    65â†’function getInitials(name: string): string {
    66â†’  const parts = name.trim().split(/\s+/);
    67â†’  if (parts.length === 1) {
    68â†’    return parts[0].charAt(0).toUpperCase();
    69â†’  }
    70â†’  return (parts[0].charAt(0) + parts[parts.length - 1].charAt(0)).toUpperCase();
    71â†’}
    72â†’
    73â†’// FunÃ§Ã£o para obter cor de fundo baseada no nome
    74â†’function getBackgroundColor(name: string): string {
    75â†’  const index = name.split('').reduce((acc, char) => acc + char.charCodeAt(0), 0);
    76â†’  return backgroundColors[index % backgroundColors.length];
    77â†’}
