    90â†’  const sizeConfig = sizeStyles[size];
    91â†’  const showFallback = !src || imageError;
    92â†’  const initials = name ? getInitials(name) : '?';
    93â†’  const bgColor = name ? getBackgroundColor(name) : 'bg-surface-400';
    94â†’
    95â†’  const containerStyles = `
    96â†’    relative inline-flex items-center justify-center
    97â†’    rounded-full overflow-hidden shrink-0
