   109â†’            w-full h-full
   110â†’            ${bgColor} text-white font-medium
   111â†’            ${sizeConfig.text}
   112â†’          `.trim().replace(/\s+/g, ' ')}
   113â†’          role="img"
   114â†’          aria-label={alt || name || 'Avatar'}
