   122â†’          onError={() => setImageError(true)}
   123â†’          className="w-full h-full object-cover"
   124â†’          {...props}
   125â†’        />
   126â†’      )}
   127â†’
   128â†’      {/* Indicador de status */}
   129â†’      {status && (
   130â†’        <span
   131â†’          className={`
   132â†’            absolute bottom-0 right-0
   133â†’            rounded-full ring-white
   134â†’            ${statusColors[status]}
   135â†’            ${sizeConfig.status}
   136â†’          `.trim().replace(/\s+/g, ' ')}
   137â†’          aria-label={`Status: ${status}`}
   138â†’        />
   139â†’      )}
