   144â†’// Componente para grupo de avatars
   145â†’interface AvatarGroupProps {
   146â†’  children: React.ReactNode;
   147â†’  max?: number;
   148â†’  size?: AvatarSize;
   149â†’}
   150â†’
   151â†’export function AvatarGroup({ children, max, size = 'md' }: AvatarGroupProps) {
   152â†’  const childArray = Array.isArray(children) ? children : [children];
   153â†’  const displayChildren = max ? childArray.slice(0, max) : childArray;
   154â†’  const remaining = max ? childArray.length - max : 0;
   155â†’
   156â†’  return (
   157â†’    <div className="flex -space-x-2">
   158â†’      {displayChildren.map((child, index) => (
   159â†’        <div
   160â†’          key={index}
   161â†’          className="ring-2 ring-white rounded-full"
   162â†’        >
   163â†’          {child}
   164â†’        </div>
   165â†’      ))}
   166â†’      {remaining > 0 && (
   167â†’        <div
   168â†’          className={`
   169â†’            flex items-center justify-center
   170â†’            rounded-full ring-2 ring-white
   171â†’            bg-surface-200 text-surface-600 font-medium
   172â†’            ${sizeStyles[size].container}
   173â†’            ${sizeStyles[size].text}
   174â†’          `.trim().replace(/\s+/g, ' ')}
   175â†’        >
   176â†’          +{remaining}
   177â†’        </div>
   178â†’      )}
   179â†’    </div>
   180â†’  );
