    14→describe('Date Formatting Functions', () => {
    15→  describe('formatDate', () => {
    16→    it('should format a date with default options', () => {
    17→      const date = new Date('2024-01-15T10:30:00');
    18→      const result = formatDate(date);
    19→
    20→      // Formato padrão: DD de MMM de YYYY
    21→      expect(result).toMatch(/15 de jan\. de 2024/);
    22→    });
    23→
    24→    it('should format a date string', () => {
    25→      const dateString = '2024-12-25T00:00:00';
    26→      const result = formatDate(dateString);
    27→
    28→      expect(result).toMatch(/25 de dez\. de 2024/);
    29→    });
    30→
    31→    it('should format a date with custom options', () => {
    32→      const date = new Date('2024-06-10T10:30:00');
    33→      const result = formatDate(date, {
    34→        day: 'numeric',
    35→        month: 'long',
    36→        year: 'numeric',
    37→      });
    38→
    39→      expect(result).toMatch(/10 de junho de 2024/);
    40→    });
    41→
    42→    it('should handle ISO date strings', () => {
    43→      const isoDate = '2024-03-20T15:45:30.000Z';
    44→      const result = formatDate(isoDate);
    45→
    46→      expect(result).toContain('2024');
    47→      expect(result).toContain('mar');
    48→    });
    49→
    50→    it('should override default options with custom ones', () => {
    51→      const date = new Date('2024-01-15T10:30:00');
    52→      const result = formatDate(date, {
    53→        year: '2-digit',
    54→      });
    55→
    56→      // Deve usar year de 2 dígitos mas manter day e month padrão
    57→      expect(result).toMatch(/24/);
    58→    });
    59→  });
