    29→/**
    30→ * Formata uma data com hora
    31→ */
    32→export function formatDateTime(date: string | Date): string {
    33→  return new Date(date).toLocaleDateString('pt-BR', {
    34→    day: '2-digit',
    35→    month: 'short',
    36→    year: 'numeric',
    37→    hour: '2-digit',
    38→    minute: '2-digit',
    39→  });
    40→}
    41→
    42→/**
    43→ * Formata data relativa (ex: "há 2 horas")
    44→ */
    45→export function formatRelativeTime(date: string | Date): string {
    46→  const now = new Date();
    47→  const then = new Date(date);
    48→  const diffInSeconds = Math.floor((now.getTime() - then.getTime()) / 1000);
    49→
    50→  if (diffInSeconds < 60) {
    51→    return 'agora mesmo';
    52→  }
    53→
    54→  const diffInMinutes = Math.floor(diffInSeconds / 60);
    55→  if (diffInMinutes < 60) {
    56→    return `há ${diffInMinutes} ${diffInMinutes === 1 ? 'minuto' : 'minutos'}`;
    57→  }
    58→
    59→  const diffInHours = Math.floor(diffInMinutes / 60);
    60→  if (diffInHours < 24) {
    61→    return `há ${diffInHours} ${diffInHours === 1 ? 'hora' : 'horas'}`;
    62→  }
    63→
    64→  const diffInDays = Math.floor(diffInHours / 24);
    65→  if (diffInDays < 7) {
    66→    return `há ${diffInDays} ${diffInDays === 1 ? 'dia' : 'dias'}`;
    67→  }
    68→
    69→  return formatDate(date);
    70→}
