   104→export function slugify(text: string): string {
   105→  return text
   106→    .toLowerCase()
   107→    .normalize('NFD')
   108→    .replace(/[\u0300-\u036f]/g, '')
   109→    .replace(/[^a-z0-9]+/g, '-')
   110→    .replace(/(^-|-$)+/g, '');
   111→}
