   121→/**
   122→ * Formata número de forma compacta (1K, 1M, etc)
   123→ */
   124→export function formatCompactNumber(num: number): string {
   125→  if (num >= 1000000) {
   126→    return (num / 1000000).toFixed(1).replace(/\.0$/, '') + 'M';
   127→  }
   128→  if (num >= 1000) {
   129→    return (num / 1000).toFixed(1).replace(/\.0$/, '') + 'K';
   130→  }
   131→  return num.toString();
   132→}
