     1→/**
     2→ * Mock do cliente Supabase para testes
     3→ *
     4→ * Este mock simula as operações do Supabase client incluindo:
     5→ * - Autenticação (auth.getUser)
     6→ * - Queries (select, eq, single)
     7→ * - Mutations (insert, update, delete)
     8→ * - RPC functions
     9→ */
    10→
    11→type MockData = Record<string, any[]>;
    12→type MockUser = { id: string; email: string; [key: string]: any } | null;
    13→
    14→/**
    15→ * Estado mock do Supabase
    16→ */
    17→class MockSupabaseState {
    18→  private data: MockData = {};
    19→  private user: MockUser = null;
    20→  private rpcFunctions: Record<string, (...args: any[]) => any> = {};
    21→
    22→  /**
    23→   * Define o usuário autenticado para os testes
    24→   */
    25→  setUser(user: MockUser) {
    26→    this.user = user;
    27→  }
    28→
    29→  /**
    30→   * Obtém o usuário autenticado
    31→   */
    32→  getUser(): MockUser {
    33→    return this.user;
    34→  }
    35→
    36→  /**
    37→   * Define dados mock para uma tabela
    38→   */
    39→  setData(table: string, data: any[]) {
    40→    this.data[table] = data;
    41→  }
    42→
    43→  /**
    44→   * Obtém dados mock de uma tabela
    45→   */
    46→  getData(table: string): any[] {
    47→    return this.data[table] || [];
    48→  }
    49→
    50→  /**
    51→   * Adiciona um registro a uma tabela
    52→   */
    53→  addData(table: string, record: any) {
    54→    if (!this.data[table]) {
    55→      this.data[table] = [];
    56→    }
    57→    this.data[table].push(record);
    58→  }
    59→
    60→  /**
    61→   * Atualiza registros em uma tabela
    62→   */
    63→  updateData(table: string, predicate: (item: any) => boolean, updates: any) {
    64→    if (!this.data[table]) return;
    65→    this.data[table] = this.data[table].map(item =>
    66→      predicate(item) ? { ...item, ...updates } : item
    67→    );
    68→  }
    69→
    70→  /**
    71→   * Remove registros de uma tabela
    72→   */
    73→  deleteData(table: string, predicate: (item: any) => boolean) {
    74→    if (!this.data[table]) return;
    75→    this.data[table] = this.data[table].filter(item => !predicate(item));
    76→  }
    77→
    78→  /**
    79→   * Registra uma função RPC mock
    80→   */
    81→  setRpcFunction(name: string, fn: (...args: any[]) => any) {
    82→    this.rpcFunctions[name] = fn;
    83→  }
    84→
    85→  /**
    86→   * Executa uma função RPC mock
    87→   */
    88→  callRpc(name: string, params: any): any {
    89→    if (this.rpcFunctions[name]) {
    90→      return this.rpcFunctions[name](params);
    91→    }
    92→    return null;
    93→  }
    94→
    95→  /**
    96→   * Reseta todo o estado mock
    97→   */
    98→  reset() {
    99→    this.data = {};
   100→    this.user = null;
   101→    this.rpcFunctions = {};
   102→  }
   103→}
   104→
   105→// Instância singleton do estado mock
   106→export const mockSupabaseState = new MockSupabaseState();
   107→
   108→/**
   109→ * Query builder mock - simula operações de query do Supabase
   110→ */
   111→class MockQueryBuilder {
   112→  private table: string;
   113→  private selectedFields: string = '*';
   114→  private filters: Array<{ field: string; value: any }> = [];
   115→  private shouldReturnSingle: boolean = false;
   116→
   117→  constructor(table: string) {
   118→    this.table = table;
   119→  }
   120→
   121→  /**
   122→   * Mock do método select()
   123→   */
   124→  select(fields: string = '*') {
   125→    this.selectedFields = fields;
   126→    return this;
   127→  }
   128→
   129→  /**
   130→   * Mock do método eq() - adiciona filtro de igualdade
   131→   */
   132→  eq(field: string, value: any) {
   133→    this.filters.push({ field, value });
   134→    return this;
   135→  }
   136→
   137→  /**
   138→   * Mock do método single() - marca para retornar um único resultado
   139→   */
   140→  single() {
   141→    this.shouldReturnSingle = true;
   142→    return this;
   143→  }
   144→
   145→  /**
   146→   * Executa a query e retorna os resultados
   147→   */
   148→  async then(resolve: (value: any) => void) {
   149→    let data = mockSupabaseState.getData(this.table);
   150→
   151→    // Aplica filtros
   152→    for (const filter of this.filters) {
   153→      data = data.filter(item => item[filter.field] === filter.value);
   154→    }
   155→
   156→    // Se for single, retorna apenas um resultado
   157→    if (this.shouldReturnSingle) {
   158→      const result = data.length > 0 ? data[0] : null;
   159→      return resolve({ data: result, error: result ? null : { message: 'No rows found' } });
   160→    }
   161→
   162→    // Retorna array de resultados
   163→    return resolve({ data, error: null });
   164→  }
   165→}
   166→
   167→/**
   168→ * Insert builder mock - simula operações de insert do Supabase
   169→ */
   170→class MockInsertBuilder {
   171→  private table: string;
   172→  private insertData: any;
   173→  private shouldSelect: boolean = false;
   174→  private shouldReturnSingle: boolean = false;
   175→
   176→  constructor(table: string, data: any) {
   177→    this.table = table;
   178→    this.insertData = data;
   179→  }
   180→
   181→  /**
   182→   * Mock do método select() após insert
   183→   */
   184→  select() {
   185→    this.shouldSelect = true;
   186→    return this;
   187→  }
   188→
   189→  /**
   190→   * Mock do método single() após insert
   191→   */
   192→  single() {
   193→    this.shouldReturnSingle = true;
   194→    return this;
   195→  }
   196→
   197→  /**
   198→   * Executa o insert e retorna o resultado
   199→   */
   200→  async then(resolve: (value: any) => void) {
   201→    // Gera ID se não existir
   202→    const newRecord = {
   203→      id: this.insertData.id || `mock-id-${Date.now()}`,
   204→      ...this.insertData,
   205→      created_at: this.insertData.created_at || new Date().toISOString(),
   206→    };
   207→
   208→    // Adiciona aos dados mock
   209→    mockSupabaseState.addData(this.table, newRecord);
   210→
   211→    if (this.shouldSelect) {
   212→      const result = this.shouldReturnSingle ? newRecord : [newRecord];
   213→      return resolve({ data: result, error: null });
   214→    }
   215→
   216→    return resolve({ data: null, error: null });
   217→  }
   218→}
