   220→/**
   221→ * Update builder mock - simula operações de update do Supabase
   222→ */
   223→class MockUpdateBuilder {
   224→  private table: string;
   225→  private updateData: any;
   226→  private filters: Array<{ field: string; value: any }> = [];
   227→
   228→  constructor(table: string, data: any) {
   229→    this.table = table;
   230→    this.updateData = data;
   231→  }
   232→
   233→  /**
   234→   * Mock do método eq() após update
   235→   */
   236→  eq(field: string, value: any) {
   237→    this.filters.push({ field, value });
   238→    return this;
   239→  }
   240→
   241→  /**
   242→   * Executa o update
   243→   */
   244→  async then(resolve: (value: any) => void) {
   245→    // Cria predicado baseado nos filtros
   246→    const predicate = (item: any) => {
   247→      return this.filters.every(filter => item[filter.field] === filter.value);
   248→    };
   249→
   250→    // Atualiza os dados
   251→    mockSupabaseState.updateData(this.table, predicate, this.updateData);
   252→
   253→    return resolve({ data: null, error: null });
   254→  }
   255→}
