   279→  async then(resolve: (value: any) => void) {
   280→    // Cria predicado baseado nos filtros
   281→    const predicate = (item: any) => {
   282→      return this.filters.every(filter => item[filter.field] === filter.value);
   283→    };
   284→
   285→    // Remove os dados
   286→    mockSupabaseState.deleteData(this.table, predicate);
   287→
   288→    return resolve({ data: null, error: null });
   289→  }
   290→}
